$.fn.standOut=function(){var a=$(this).parents().get().reverse();if(1<a.length)for(var b=1;b<=a.length;b++)b==a.length?$(a[b-1]).children().not($(this)).addClass("blurStandOutMe"):$(a[b-1]).children().not(a[b]).addClass("blurStandOutMe")};$.fn.standOff=function(){$(".blurStandOutMe").removeClass("blurStandOutMe")};$.fn.standOutMe=function(){$(this).focus(function(){$(this).standOut()}).blur(function(){$(this).standOff()})};