    private void sendMail(String from, String fromName, String to, String[] toCC, String subject, String message, String contentType, String templateId, List<Attachment> attached, Map<String, String> customArgs, String hourOfDay, JSONObject jsonVariables) {



        Content content = null;
        if (templateId != null) {

            // Validate that all the variables to replace exists
            if(jsonVariables != null){
                if(JsonUtil.getStringFromJson(jsonVariables, "unsubscribe_display", null) == null){
                    jsonVariables.put("unsubscribe_display", "none");
                }
            }else{
                jsonVariables = new JSONObject();
                jsonVariables.put("unsubscribe_display", "none");
            }

            content = new Content(contentType, replaceVariables(localTemplates.get(templateId), jsonVariables));
        } else {
            content = new Content(contentType, replaceVariables(message, jsonVariables));
        }


        if (!Configuration.hostEnvIsProduction())
            subject = subject + " " + Configuration.hostEnvName();

        Email efrom = new Email(from);
        if (fromName != null) {
            efrom.setName(fromName);
        }
        Email eto = new Email(Configuration.hostEnvIsProduction() ? to : Configuration.EMAIL_DEV_TO);
        CustomSendgridMail mail = new CustomSendgridMail(efrom, subject, eto, content);

        if (attached != null) {
            for (Attachment a : attached) {
                Attachments atts = new Attachments();
                atts.setContent(a.getContent());
                atts.setFilename(a.getFilename());
                mail.addAttachments(atts);
            }
        }

        if (customArgs != null) {
            customArgs.forEach((k, v) -> mail.addCustomArg(k, v));
        }

        if (toCC != null && toCC.length > 0 && Configuration.hostEnvIsProduction()) {
            mail.setCC(toCC);
        }

        // API v3
//        emailExecutor.execute(() -> {
            Request request = new Request();
            try {
                request.method = Method.POST;
                request.endpoint = "mail/send";
                request.body = mail.build();
                Response response = sg.api(request);
            } catch (Exception ex) {
                logger.error("Alert sending email", ex);
            }
//        });
    }

    @Override
    public void sendPlainMail(String from, String fromName, String to, String[] cc, String subject, String message, String templateId, List<Attachment> attached, Map<String, String> customArgs, String hourOfDay) {
        sendMail(from, fromName, to, cc, subject, message, ctPlain, templateId, attached, customArgs, hourOfDay, null);
    }

    @Override
    public void sendHtmlMail(String from, String fromName, String to, String[] cc, String subject, String message, String templateId, List<Attachment> attached, Map<String, String> customArgs, String hourOfDay, JSONObject jsonVariables) {
        sendMail(from, fromName, to, cc, subject, message, ctHtml, templateId, attached, customArgs, hourOfDay, jsonVariables);
    }

    private String replaceVariables(String message, JSONObject jsonReplace) {
        if (message == null) {
            return null;
        }
        if (jsonReplace == null) {
            return message;
        }

        Iterator<?> keys = jsonReplace.keys();
        while (keys.hasNext()) {
            String key = (String) keys.next();
//            SENDGRID VARIABLES
            message = message.replaceAll("%" + key.toLowerCase() + "%", jsonReplace.getString(key));
            message = message.replaceAll("%" + key.toUpperCase() + "%", jsonReplace.getString(key));
//            AWS SES VARIABLES
            message = message.replaceAll("(\\{\\{ |\\{\\{)" + key.toLowerCase() + "( \\}\\}|\\}\\})", jsonReplace.getString(key));
            message = message.replaceAll("(\\{\\{ |\\{\\{)" + key.toUpperCase() + "( \\}\\}|\\}\\})", jsonReplace.getString(key));
        }
        return message;
    }
